import streamlit as st

def home():
    st.title('Syarat Pendaftaran KIP Kuliah')

    st.image('data:image/jpeg;base64,', width=400)
    
    st.header('Syarat Pendaftaran Khusus')
    st.write('1. Jenjang Pendidikan minimal D1 atau S1 dan maksimal S2')
    st.write('2. Akreditasi Perguruan Tinggi minimal akreditasi B')
    st.write('3. Akreditasi Program Studi minimal akreditasi C')
    st.write('4. Nilai IPK Semester Terakhir nilai IPK semester terakhir harus kurang dari 2.50')

    st.header('Syarat Umum')
    st.write('1. Warga Negara Indonesia (WNI)')
    st.write('2. Siswa SMA/sederajat kelas X atau XI yang terdaftar di Dapodik')
    st.write('3. Memiliki potensi akademik yang baik')
    st.write('4. Tidak sedang menerima beasiswa dari lembaga lain')

    st.header('Syarat Administrasi')
    st.write('1. Kartu Keluarga (KK)')
    st.write('2. Kartu Tanda Penduduk (KTP) atau Surat Keterangan Kependudukan (SKK)')
    st.write('3. Surat Keterangan Tidak Mampu (SKTM) dari desa/kelurahan')
    st.write('4. Surat keterangan penghasilan orang tua/wali')
    st.write('5. Surat keterangan kepemilikan aset')
    st.write('6. Foto rumah yang menunjukkan kondisi ekonomi keluarga')
    st.write('7. Ijazah dan transkrip nilai SMP/sederajat')
    st.write('8. Surat keterangan sehat dari puskesmas atau rumah sakit')
    st.write('9. Surat pernyataan belum menikah')
    st.write('10. Surat pernyataan tidak pernah terlibat kasus hukum')
    st.write('11. Surat pernyataan bersedia mengikuti segala ketentuan dan peraturan beasiswa KIP-K')

if __name__ == "__main__":
    home()
